pub fn run(part: i8) {
    if part == 1 {
        part1();
    } else {
        part2();
    }
}

fn part1() {
    let shapes = Shape::make_shapes();
    println!("{:#?}", shapes);
    let dirs = parse_input();
    println!("{:?}", dirs);
}

fn part2() {
}

#[derive(Debug)]
struct Shape {
    rows: Vec<Vec<bool>>,
}

impl Shape {
    fn make_shapes() -> Vec<Self> {
        let mut v = vec![];

        // ####
        v.push(Self{
            rows: vec![vec![true; 4]],
        });

        // .#.
        // ###
        // .#.
        v.push(Self{
            rows: vec![vec![false, true, false], vec![true; 3], vec![false, true, false]],
        });

        // ..#
        // ..#
        // ###
        v.push(Self{
            rows: vec![vec![false, false, true], vec![false, false, true], vec![true; 3]],
        });

        // #
        // #
        // #
        // #
        v.push(Self{
            rows: vec![vec![true], vec![true], vec![true], vec![true]],
        });

        // ##
        // ##
        v.push(Self{
            rows: vec![vec![true; 2], vec![true; 2]],
        });

        v
    }
}

#[derive(Debug)]
enum Dir {
    Left,
    Right,
}

fn parse_input() -> Vec<Dir> {
    input().bytes().map(|b| {
        match b {
            b'<' => Dir::Left,
            b'>' => Dir::Right,
            _ => panic!("{}", b),
        }
    }).collect()
}

